from collections import Counter
import time

def minimalKSum(nums, k):
    i=1
    minSum=0
    start_time = time.time()
    hashMap = dict(Counter(nums))
    print("--- %s seconds ---" % (time.time() - start_time))
    
    while(k>0 ):
        if hashMap.get(i) is None:
            minSum+=i
            k-=1
        i+=1
    return minSum

start_time = time.time()

